package org.apache.dolphinscheduler.dao.mapper;

import org.apache.dolphinscheduler.dao.entity.User;
import com.baomidou.mybatisplus.core.mapper.BaseMapper;

import java.util.List;

/**
 * user mapper interface
 */
public interface UserMapper extends BaseMapper<User> {

    /**
     * query user by name
     *
     * @param userName name
     * @return user
     */
    User queryByUserNameAccurately(String userName);

    /**
     * query details by id
     *
     * @param userId user id
     * @return user
     */
    User queryDetailsById(int userId);

    /**
     * query user list by alert group id
     *
     * @param alertGroupId alert group id
     * @return user list
     */
    List<User> queryUserListByAlertGroupId(Integer alertGroupId);


    /**
     * query all general user
     *
     * @return user list
     */
    List<User> queryAllGeneralUser();
}
