package org.argouml.diagram;

public class FigDimensions {
    private final int x;
    private final int y;
    private final int width;
    private final int height;

    public FigDimensions(int x, int y, int width, int height) {
        this.x = x;
        this.y = y;
        this.width = width;
        this.height = height;
    }

    public int getX() {
        return x;
    }

    public int getY() {
        return y;
    }

    public int getWidth() {
        return width;
    }

    public int getHeight() {
        return height;
    }
}
