package org.argouml.ui;

import javax.swing.plaf.ColorUIResource;

public class ThemeColors {
    private final ColorUIResource color1, color2, color3;

    public ThemeColors(int red, int green, int blue) {
        color1 = new ColorUIResource(red, green, blue);
        color2 = new ColorUIResource(red + 51, green + 51, blue + 51);
        color3 = new ColorUIResource(red + 102, green + 102, blue + 102);
    }

    public ColorUIResource getColor1() {
        return color1;
    }

    public ColorUIResource getColor2() {
        return color2;
    }

    public ColorUIResource getColor3() {
        return color3;
    }
}

package org.argouml.ui;

import java.awt.Font;
import javax.swing.plaf.FontUIResource;

public class ThemeFonts {
    private final FontUIResource controlFont, systemFont, userFont, smallFont, windowTitleFont;

    public ThemeFonts(String fontName, int fontSize) {
        controlFont = new FontUIResource(fontName, Font.PLAIN, fontSize);
        systemFont = new FontUIResource(fontName, Font.PLAIN, fontSize);
        userFont = new FontUIResource(fontName, Font.PLAIN, fontSize);
        smallFont = new FontUIResource(fontName, Font.PLAIN, fontSize - 2);
        windowTitleFont = new FontUIResource(fontName, Font.BOLD, fontSize);
    }

    public FontUIResource getControlFont() {
        return controlFont;
    }

    public FontUIResource getSystemFont() {
        return systemFont;
    }

    public FontUIResource getUserFont() {
        return userFont;
    }

    public FontUIResource getSmallFont() {
        return smallFont;
    }

    public FontUIResource getWindowTitleFont() {
        return windowTitleFont;
    }
}
