package org.argouml.ui;

import javax.swing.plaf.ColorUIResource;

class ThemeColor {
    private ColorUIResource primary1;
    private ColorUIResource primary2;
    private ColorUIResource primary3;

    ThemeColor(int shade1, int shade2, int shade3) {
        primary1 = new ColorUIResource(shade1, shade1, shade1 + 51);
        primary2 = new ColorUIResource(shade2, shade2, shade2 + 51);
        primary3 = new ColorUIResource(shade3, shade3, shade3 + 51);
    }

    ColorUIResource getPrimary1() { return primary1; }
    ColorUIResource getPrimary2() { return primary2; }
    ColorUIResource getPrimary3() { return primary3; }

    ColorUIResource getSecondary1() { return primary1; }
    ColorUIResource getSecondary2() { return primary2; }
    ColorUIResource getSecondary3() { return primary3; }
}

