package org.argouml.ui;

import javax.swing.plaf.ColorUIResource;

public class ThemeColors {
    private final ColorUIResource primary1;
    private final ColorUIResource primary2;
    private final ColorUIResource primary3;
    private final ColorUIResource secondary1;
    private final ColorUIResource secondary2;
    private final ColorUIResource secondary3;

    public ThemeColors(int primary, int secondary, int tertiary) {
        this.primary1 = new ColorUIResource(primary, primary, tertiary);
        this.primary2 = new ColorUIResource(primary, secondary, tertiary);
        this.primary3 = new ColorUIResource(secondary, secondary, tertiary);
        this.secondary1 = new ColorUIResource(primary, primary, primary);
        this.secondary2 = new ColorUIResource(primary, secondary, secondary);
        this.secondary3 = new ColorUIResource(secondary, secondary, secondary);
    }

    public ColorUIResource primary1() { return primary1; }
    public ColorUIResource primary2() { return primary2; }
    public ColorUIResource primary3() { return primary3; }
    public ColorUIResource secondary1() { return secondary1; }
    public ColorUIResource secondary2() { return secondary2; }
    public ColorUIResource secondary3() { return secondary3; }
}

package org.argouml.ui;

import java.awt.Font;
import javax.swing.plaf.FontUIResource;

public class ThemeFonts {
    private final FontUIResource controlFont;
    private final FontUIResource systemFont;
    private final FontUIResource userFont;
    private final FontUIResource smallFont;
    private final FontUIResource windowTitleFont;

    public ThemeFonts(String controlFontName, String systemFontName, int largeSize, int smallSize) {
        this.controlFont = new FontUIResource(controlFontName, Font.PLAIN, largeSize);
        this.systemFont = new FontUIResource(systemFontName, Font.PLAIN, largeSize);
        this.userFont = new FontUIResource(controlFontName, Font.PLAIN, largeSize);
        this.smallFont = new FontUIResource(systemFontName, Font.PLAIN, smallSize);
        this.windowTitleFont = new FontUIResource(controlFontName, Font.BOLD, largeSize);
    }

    public FontUIResource controlFont() { return controlFont; }
    public FontUIResource systemFont() { return systemFont; }
    public FontUIResource userFont() { return userFont; }
    public FontUIResource smallFont() { return smallFont; }
    public FontUIResource windowTitleFont() { return windowTitleFont; }
}
