package org.argouml.ui;

import javax.swing.plaf.ColorUIResource;

public class ThemeColorSet {

    private final ColorUIResource primary1, primary2, primary3;
    private final ColorUIResource secondary1, secondary2, secondary3;

    public ThemeColorSet(int p1r, int p1g, int p1b, int p2r, int p2g, int p2b, int p3r, int p3g, int p3b) {
        primary1 = new ColorUIResource(p1r, p1g, p1b);
        primary2 = new ColorUIResource(p2r, p2g, p2b);
        primary3 = new ColorUIResource(p3r, p3g, p3b);
        secondary1 = new ColorUIResource(p1r, p1g, p1b);
        secondary2 = new ColorUIResource(p2r, p2g, p2b);
        secondary3 = new ColorUIResource(p3r, p3g, p3b);
    }

    public ColorUIResource getPrimary1() { return primary1; }
    public ColorUIResource getPrimary2() { return primary2; }
    public ColorUIResource getPrimary3() { return primary3; }
    public ColorUIResource getSecondary1() { return secondary1; }
    public ColorUIResource getSecondary2() { return secondary2; }
    public ColorUIResource getSecondary3() { return secondary3; }
}

package org.argouml.ui;

import java.awt.Font;
import javax.swing.plaf.FontUIResource;

public class ThemeFontSet {

    private final FontUIResource controlFont, systemFont, userFont, smallFont, windowTitleFont;

    public ThemeFontSet(String controlName, String systemName, int controlStyle, int systemStyle, int controlSize, int smallSize) {
        controlFont = new FontUIResource(controlName, controlStyle, controlSize);
        systemFont = new FontUIResource(systemName, systemStyle, controlSize);
        userFont = new FontUIResource(controlName, controlStyle, controlSize);
        smallFont = new FontUIResource(systemName, systemStyle, smallSize);
        windowTitleFont = new FontUIResource(controlName, Font.BOLD, controlSize);
    }

    public FontUIResource getControlFont() { return controlFont; }
    public FontUIResource getSystemFont() { return systemFont; }
    public FontUIResource getUserFont() { return userFont; }
    public FontUIResource getSmallFont() { return smallFont; }
    public FontUIResource getWindowTitleFont() { return windowTitleFont; }
}
