package org.argouml.ui;

import javax.swing.plaf.ColorUIResource;

public class ThemeColor {
    private final ColorUIResource color1;
    private final ColorUIResource color2;
    private final ColorUIResource color3;

    public ThemeColor(int shade1, int shade2, int shade3) {
        color1 = new ColorUIResource(shade1, shade1, shade1 + 51);
        color2 = new ColorUIResource(shade2, shade2, shade2 + 51);
        color3 = new ColorUIResource(shade3, shade3, shade3 + 51);
    }

    public ColorUIResource getColor1() {
        return color1;
    }

    public ColorUIResource getColor2() {
        return color2;
    }

    public ColorUIResource getColor3() {
        return color3;
    }
}

package org.argouml.ui;

import java.awt.Font;
import javax.swing.plaf.FontUIResource;

public class ThemeFont {
    private final FontUIResource controlFont;
    private final FontUIResource systemFont;
    private final FontUIResource windowTitleFont;
    private final FontUIResource userFont;
    private final FontUIResource smallFont;

    public ThemeFont(String controlFontName, String systemFontName, int mainFontSize, int smallFontSize) {
        controlFont = new FontUIResource(controlFontName, Font.PLAIN, mainFontSize);
        systemFont = new FontUIResource(systemFontName, Font.PLAIN, mainFontSize);
        windowTitleFont = new FontUIResource(controlFontName, Font.BOLD, mainFontSize);
        userFont = new FontUIResource(controlFontName, Font.PLAIN, mainFontSize);
        smallFont = new FontUIResource(systemFontName, Font.PLAIN, smallFontSize);
    }

    public FontUIResource getControlFont() {
        return controlFont;
    }

    public FontUIResource getSystemFont() {
        return systemFont;
    }

    public FontUIResource getWindowTitleFont() {
        return windowTitleFont;
    }

    public FontUIResource getUserFont() {
        return userFont;
    }

    public FontUIResource getSmallFont() {
        return smallFont;
    }
}
