package org.argouml.ui;

import java.awt.Font;
import javax.swing.plaf.FontUIResource;

public class ThemeFont {

    private final FontUIResource controlFont;
    private final FontUIResource systemFont;
    private final FontUIResource windowTitleFont;
    private final FontUIResource userFont;

    public ThemeFont(String controlFontName, String systemFontName, int fontSize, int fontStyle, int titleFontStyle) {
        this.controlFont = new FontUIResource(controlFontName, fontStyle, fontSize);
        this.systemFont = new FontUIResource(systemFontName, fontStyle, fontSize);
        this.windowTitleFont = new FontUIResource(controlFontName, titleFontStyle, fontSize);
        this.userFont = new FontUIResource(controlFontName, fontStyle, fontSize);
    }

    public FontUIResource getControlFont() { return controlFont; }
    public FontUIResource getSystemFont() { return systemFont; }
    public FontUIResource getWindowTitleFont() { return windowTitleFont; }
    public FontUIResource getUserFont() { return userFont; }

    public FontUIResource createSmallFont(int size) {
        return new FontUIResource("Dialog", Font.PLAIN, size);
    }
}
