package org.argouml.ui.theme;

import javax.swing.plaf.FontUIResource;
import java.awt.Font;

class ThemeFonts {
    private FontUIResource controlFont;
    private FontUIResource systemFont;
    private FontUIResource windowTitleFont;
    private FontUIResource userFont;
    private FontUIResource smallFont;

    ThemeFonts(String controlFontFamily, int controlFontStyle, int controlFontSize, String systemFontFamily, int systemFontStyle, int systemFontSize, String windowTitleFontFamily, int windowTitleFontStyle, int windowTitleFontSize) {
        this.controlFont = new FontUIResource(controlFontFamily, controlFontStyle, controlFontSize);
        this.systemFont = new FontUIResource(systemFontFamily, systemFontStyle, systemFontSize);
        this.windowTitleFont = new FontUIResource(windowTitleFontFamily, windowTitleFontStyle, windowTitleFontSize);
        this.userFont = new FontUIResource(controlFontFamily, controlFontStyle, controlFontSize);
        this.smallFont = new FontUIResource(systemFontFamily, systemFontStyle, controlFontSize - 2);
    }

    FontUIResource getControlFont() { return controlFont; }
    FontUIResource getSystemFont() { return systemFont; }
    FontUIResource getWindowTitleFont() { return windowTitleFont; }
    FontUIResource getUserFont() { return userFont; }
    FontUIResource getSmallFont() { return smallFont; }
}
