package org.argouml.ui;

import javax.swing.plaf.ColorUIResource;

public class ThemeColors {

    private final ColorUIResource primary1;
    private final ColorUIResource primary2;
    private final ColorUIResource primary3;
    private final ColorUIResource secondary1;
    private final ColorUIResource secondary2;
    private final ColorUIResource secondary3;

    public ThemeColors(int p1, int p2, int p3, int s1, int s2, int s3, int s4, int s5, int s6) {
        primary1 = new ColorUIResource(p1, p2, p3);
        primary2 = new ColorUIResource(s1, s2, s3);
        primary3 = new ColorUIResource(s4, s5, s6);
        secondary1 = new ColorUIResource(p1, p2, p3);
        secondary2 = new ColorUIResource(s1, s2, s3);
        secondary3 = new ColorUIResource(s4, s5, s6);
    }

    public ColorUIResource getPrimary1() {
        return primary1;
    }

    public ColorUIResource getPrimary2() {
        return primary2;
    }

    public ColorUIResource getPrimary3() {
        return primary3;
    }

    public ColorUIResource getSecondary1() {
        return secondary1;
    }

    public ColorUIResource getSecondary2() {
        return secondary2;
    }

    public ColorUIResource getSecondary3() {
        return secondary3;
    }
}

package org.argouml.ui;

import java.awt.Font;
import javax.swing.plaf.FontUIResource;

public class ThemeFonts {

    private final FontUIResource controlFont;
    private final FontUIResource systemFont;
    private final FontUIResource windowTitleFont;
    private final FontUIResource userFont;
    private final FontUIResource smallFont;

    public ThemeFonts(String controlFontFamily, String systemFontFamily, int controlFontSize, int systemFontSize, int windowTitleFontSize, int smallFontSize) {
        controlFont = new FontUIResource(controlFontFamily, Font.PLAIN, controlFontSize);
        systemFont = new FontUIResource(systemFontFamily, Font.PLAIN, systemFontSize);
        windowTitleFont = new FontUIResource(controlFontFamily, Font.BOLD, windowTitleFontSize);
        userFont = new FontUIResource(controlFontFamily, Font.PLAIN, controlFontSize);
        smallFont = new FontUIResource(systemFontFamily, Font.PLAIN, smallFontSize);
    }

    public FontUIResource getControlFont() {
        return controlFont;
    }

    public FontUIResource getSystemFont() {
        return systemFont;
    }

    public FontUIResource getWindowTitleFont() {
        return windowTitleFont;
    }

    public FontUIResource getUserFont() {
        return userFont;
    }

    public FontUIResource getSmallFont() {
        return smallFont;
    }
}
