package org.argouml.sequence2.diagram;

import java.awt.Color;
import org.tigris.gef.presentation.FigLine;
import org.tigris.gef.presentation.FigRect;

public class FigLineRect {
    private FigLine line;
    private FigRect rect;

    public FigLineRect(int x, int y, int width, int height, Color lineColor, int lineWidth) {
        rect = new FigRect(x, y, width, height);
        rect.setFilled(false);
        rect.setLineWidth(0);
        line = new FigLine(x + width / 2, y, x + width / 2, y + height, lineColor);
        line.setDashed(true);
        line.setLineWidth(lineWidth);
    }

    public void setBounds(int x, int y, int w, int h) {
        rect.setBounds(x, y, w, h);
        line.setBounds(x + w / 2, y, w, h);
    }

    public void setLineWidth(int w) {
        line.setLineWidth(w);
    }

    public FigRect getFigRect() {
        return rect;
    }

    public FigLine getFigLine() {
        return line;
    }

    public int getX() {
        return rect.getX();
    }

    public int getY() {
        return rect.getY();
    }

    public int getWidth() {
        return rect.getWidth();
    }

    public int getHeight() {
        return rect.getHeight();
    }

    // Potentially other methods
}
