package org.argouml.ui;

import javax.swing.plaf.ColorUIResource;

public class ThemeColors {
    private final ColorUIResource color1;
    private final ColorUIResource color2;
    private final ColorUIResource color3;

    public ThemeColors(int colorValue1, int colorValue2, int colorValue3) {
        color1 = new ColorUIResource(colorValue1, colorValue1, colorValue1 + 51);
        color2 = new ColorUIResource(colorValue2, colorValue2, colorValue2 + 51);
        color3 = new ColorUIResource(colorValue3, colorValue3, colorValue3 + 51);
    }

    public ColorUIResource getColor1() {
        return color1;
    }

    public ColorUIResource getColor2() {
        return color2;
    }

    public ColorUIResource getColor3() {
        return color3;
    }
}

package org.argouml.ui;

import java.awt.Font;
import javax.swing.plaf.FontUIResource;

public class ThemeFonts {
    private final FontUIResource controlFont;
    private final FontUIResource systemFont;
    private final FontUIResource windowTitleFont;
    private final FontUIResource userFont;
    private final FontUIResource smallFont;

    public ThemeFonts(String primaryFontName, String secondaryFontName, int fontSizeLarge, int fontSizeSmall) {
        controlFont = new FontUIResource(primaryFontName, Font.BOLD, fontSizeLarge);
        systemFont = new FontUIResource(secondaryFontName, Font.PLAIN, fontSizeLarge);
        windowTitleFont = new FontUIResource(primaryFontName, Font.BOLD, fontSizeLarge);
        userFont = new FontUIResource(primaryFontName, Font.PLAIN, fontSizeLarge);
        smallFont = new FontUIResource(secondaryFontName, Font.PLAIN, fontSizeSmall);
    }

    public FontUIResource getControlFont() {
        return controlFont;
    }

    public FontUIResource getSystemFont() {
        return systemFont;
    }

    public FontUIResource getUserFont() {
        return userFont;
    }

    public FontUIResource getSmallFont() {
        return smallFont;
    }

    public FontUIResource getWindowTitleFont() {
        return windowTitleFont;
    }
}
