package org.argouml.ui;

import java.awt.Font;
import javax.swing.plaf.FontUIResource;

class ThemeFonts {
    private final FontUIResource controlFont;
    private final FontUIResource systemFont;
    private final FontUIResource windowTitleFont;
    private final FontUIResource userFont;
    private final FontUIResource smallFont;

    ThemeFonts(String sansSerif, String dialog, int regFontSize, int smallFontSize) {
        this.controlFont = new FontUIResource(sansSerif, Font.PLAIN, regFontSize);
        this.systemFont = new FontUIResource(dialog, Font.PLAIN, regFontSize);
        this.windowTitleFont = new FontUIResource(sansSerif, Font.BOLD, regFontSize);
        this.userFont = new FontUIResource(sansSerif, Font.PLAIN, regFontSize);
        this.smallFont = new FontUIResource(dialog, Font.PLAIN, smallFontSize);
    }

    // Getters for each font
}

