package org.argouml.ui;

import javax.swing.plaf.ColorUIResource;

public class ThemeColors {

    private final ColorUIResource primary1;
    private final ColorUIResource primary2;
    private final ColorUIResource primary3;

    private final ColorUIResource secondary1;
    private final ColorUIResource secondary2;
    private final ColorUIResource secondary3;

    public ThemeColors(
            int primary1Red, int primary1Green, int primary1Blue,
            int primary2Red, int primary2Green, int primary2Blue,
            int primary3Red, int primary3Green, int primary3Blue,
            int secondary1Red, int secondary1Green, int secondary1Blue,
            int secondary2Red, int secondary2Green, int secondary2Blue,
            int secondary3Red, int secondary3Green, int secondary3Blue) {
        primary1 = new ColorUIResource(primary1Red, primary1Green, primary1Blue);
        primary2 = new ColorUIResource(primary2Red, primary2Green, primary2Blue);
        primary3 = new ColorUIResource(primary3Red, primary3Green, primary3Blue);
        secondary1 = new ColorUIResource(secondary1Red, secondary1Green, secondary1Blue);
        secondary2 = new ColorUIResource(secondary2Red, secondary2Green, secondary2Blue);
        secondary3 = new ColorUIResource(secondary3Red, secondary3Green, secondary3Blue);
    }

    public ColorUIResource getPrimary1() { return primary1; }

    public ColorUIResource getPrimary2() { return primary2; }

    public ColorUIResource getPrimary3() { return primary3; }

    public ColorUIResource getSecondary1() { return secondary1; }

    public ColorUIResource getSecondary2() { return secondary2; }

    public ColorUIResource getSecondary3() { return secondary3; }
}

