package org.argouml.ui;

import javax.swing.plaf.ColorUIResource;

public class ThemeColors {

	private ColorUIResource color1;
	private ColorUIResource color2;
	private ColorUIResource color3;

	public ThemeColors(int r1, int g1, int b1, int r2, int g2, int b2, int r3, int g3, int b3) {
		this.color1 = new ColorUIResource(r1, g1, b1);
		this.color2 = new ColorUIResource(r2, g2, b2);
		this.color3 = new ColorUIResource(r3, g3, b3);
	}

	public ColorUIResource getColor1() {
		return color1;
	}

	public ColorUIResource getColor2() {
		return color2;
	}

	public ColorUIResource getColor3() {
		return color3;
	}
}

package org.argouml.ui;

import java.awt.Font;
import javax.swing.plaf.FontUIResource;

public class ThemeFonts {

	private FontUIResource controlFont;
	private FontUIResource systemFont;
	private FontUIResource windowTitleFont;
	private FontUIResource userFont;
	private FontUIResource smallFont;

	public ThemeFonts(String fontType, int textStyle, int fontSize) {
		this.controlFont = new FontUIResource(fontType, textStyle, fontSize);
		this.systemFont = new FontUIResource("Dialog", Font.PLAIN, fontSize);
		this.windowTitleFont = new FontUIResource(fontType, Font.BOLD, fontSize);
		this.userFont = new FontUIResource(fontType, Font.PLAIN, fontSize);
		this.smallFont = new FontUIResource("Dialog", Font.PLAIN, fontSize - 2);
	}

	public FontUIResource getControlFont() {
		return controlFont;
	}

	public FontUIResource getSystemFont() {
		return systemFont;
	}

	public FontUIResource getWindowTitleFont() {
		return windowTitleFont;
	}

	public FontUIResource getUserFont() {
		return userFont;
	}

	public FontUIResource getSmallFont() {
		return smallFont;
	}
}

