package org.argouml.ui;

import java.awt.Font;
import javax.swing.plaf.FontUIResource;

public class ThemeFonts {
    private final FontUIResource controlFont;
    private final FontUIResource systemFont;
    private final FontUIResource windowTitleFont;
    private final FontUIResource userFont;
    private final FontUIResource smallFont;

    public ThemeFonts(String mainFontFamily, String smallFontFamily, int mainFontSize, int smallFontSize) {
        controlFont = new FontUIResource(mainFontFamily, Font.PLAIN, mainFontSize);
        systemFont = new FontUIResource(mainFontFamily, Font.PLAIN, mainFontSize);
        windowTitleFont = new FontUIResource(mainFontFamily, Font.BOLD, mainFontSize);
        userFont = new FontUIResource(mainFontFamily, Font.PLAIN, mainFontSize);
        smallFont = new FontUIResource(smallFontFamily, Font.PLAIN, smallFontSize);
    }

    public FontUIResource getControlTextFont() {
        return controlFont;
    }

    public FontUIResource getSystemTextFont() {
        return systemFont;
    }

    public FontUIResource getUserTextFont() {
        return userFont;
    }

    public FontUIResource getMenuTextFont() {
        return controlFont;
    }

    public FontUIResource getSubTextFont() {
        return smallFont;
    }

    public FontUIResource getWindowTitleFont() {
        return windowTitleFont;
    }
}
