public class ThemeColors {

    private final ColorUIResource primary1;
    private final ColorUIResource primary2;
    private final ColorUIResource primary3;

    public ThemeColors(int r1, int g1, int b1, int r2, int g2, int b2, int r3, int g3, int b3) {
        primary1 = new ColorUIResource(r1, g1, b1);
        primary2 = new ColorUIResource(r2, g2, b2);
        primary3 = new ColorUIResource(r3, g3, b3);
    }

    public ColorUIResource getPrimary1() {
        return primary1;
    }

    public ColorUIResource getPrimary2() {
        return primary2;
    }

    public ColorUIResource getPrimary3() {
        return primary3;
    }

    // Secondary colors are derived from primary colors
    public ColorUIResource getSecondary1() {
        return new ColorUIResource(primary1.getRGB() & 0xFFEFEFEF);
    }

    public ColorUIResource getSecondary2() {
        return new ColorUIResource(primary2.getRGB() & 0xFFDFDFDF);
    }

    public ColorUIResource getSecondary3() {
        return new ColorUIResource(primary3.getRGB() & 0xFFCFCFCF);
    }
}

public class ThemeFonts {

    private final FontUIResource controlFont;
    private final FontUIResource systemFont;
    private final FontUIResource windowTitleFont;
    private final FontUIResource userFont;
    private final FontUIResource smallFont;

    public ThemeFonts(String controlFontFamily, String systemFontFamily, int plainStyle, int boldStyle, int largeSize, int smallSize) {
        controlFont = new FontUIResource(controlFontFamily, plainStyle, largeSize);
        systemFont = new FontUIResource(systemFontFamily, plainStyle, largeSize);
        windowTitleFont = new FontUIResource(controlFontFamily, boldStyle, largeSize);
        userFont = new FontUIResource(controlFontFamily, plainStyle, largeSize);
        smallFont = new FontUIResource(systemFontFamily, plainStyle, smallSize);
    }

    public FontUIResource getControlFont() {
        return controlFont;
    }

    public FontUIResource getSystemFont() {
        return systemFont;
    }

    public FontUIResource getUserFont() {
        return userFont;
    }

    public FontUIResource getWindowTitleFont() {
        return windowTitleFont;
    }

    public FontUIResource getSmallFont() {
        return smallFont;
    }
}
