package org.argouml.ui;

import javax.swing.plaf.ColorUIResource;

public class ThemeColor {
    private final ColorUIResource primary1;
    private final ColorUIResource primary2;
    private final ColorUIResource primary3;

    public ThemeColor(int primaryBase, int secondaryBase, int tertiaryBase) {
        primary1 = new ColorUIResource(primaryBase, primaryBase, secondaryBase);
        primary2 = new ColorUIResource(secondaryBase, secondaryBase, tertiaryBase);
        primary3 = new ColorUIResource(tertiaryBase, tertiaryBase, 255);
    }

    public ColorUIResource getPrimary1() {
        return primary1;
    }

    public ColorUIResource getPrimary2() {
        return primary2;
    }

    public ColorUIResource getPrimary3() {
        return primary3;
    }
}

package org.argouml.ui;

import java.awt.Font;
import javax.swing.plaf.FontUIResource;

public class ThemeFont {
    private final FontUIResource controlFont;
    private final FontUIResource systemFont;
    private final FontUIResource windowTitleFont;
    private final FontUIResource userFont;
    private final FontUIResource smallFont;

    public ThemeFont(Font control, Font system, Font windowTitle, int smallFontSize) {
        controlFont = new FontUIResource(control);
        systemFont = new FontUIResource(system);
        windowTitleFont = new FontUIResource(windowTitle);
        userFont = new FontUIResource(control);
        smallFont = new FontUIResource(system.getName(), system.getStyle(), smallFontSize);
    }

    public FontUIResource getControlFont() {
        return controlFont;
    }

    public FontUIResource getSystemFont() {
        return systemFont;
    }

    public FontUIResource getWindowTitleFont() {
        return windowTitleFont;
    }

    public FontUIResource getUserFont() {
        return userFont;
    }

    public FontUIResource getSmallFont() {
        return smallFont;
    }
}
