package org.argouml.ui;

import javax.swing.plaf.ColorUIResource;

public class ThemeColors {

    private final ColorUIResource color1;
    private final ColorUIResource color2;
    private final ColorUIResource color3;

    public ThemeColors(int shade1, int shade2, int shade3) {
        color1 = new ColorUIResource(shade1, shade1, shade1);
        color2 = new ColorUIResource(shade2, shade2, shade2);
        color3 = new ColorUIResource(shade3, shade3, shade3);
    }

    public ColorUIResource getColor1() { return color1; }
    public ColorUIResource getColor2() { return color2; }
    public ColorUIResource getColor3() { return color3; }
}

package org.argouml.ui;

import java.awt.Font;
import javax.swing.plaf.FontUIResource;

public class ThemeFonts {

    private final FontUIResource controlFont;
    private final FontUIResource systemFont;
    private final FontUIResource windowTitleFont;
    private final FontUIResource userFont;
    private final FontUIResource smallFont;

    public ThemeFonts(String controlFamily, String systemFamily, int largeSize, int smallSize) {
        controlFont = new FontUIResource(controlFamily, Font.PLAIN, largeSize);
        systemFont = new FontUIResource(systemFamily, Font.PLAIN, largeSize);
        windowTitleFont = new FontUIResource(controlFamily, Font.BOLD, largeSize);
        userFont = new FontUIResource(controlFamily, Font.PLAIN, largeSize);
        smallFont = new FontUIResource(systemFamily, Font.PLAIN, smallSize);
    }

    public FontUIResource getControlFont() { return controlFont; }
    public FontUIResource getSystemFont() { return systemFont; }
    public FontUIResource getWindowTitleFont() { return windowTitleFont; }
    public FontUIResource getUserFont() { return userFont; }
    public FontUIResource getSmallFont() { return smallFont; }
}

