    if (gater.test(configuration.getOpenOptions())) {
        return new FileVisitor() {
            @Override
            public void visit(File f, String relativePath) throws IOException {
                VisitorInfo visitorInfo = new VisitorInfo(f, configuration.getVerificationRoot(), configuration.getOpenOptions());
                if (configuration.getVerificationRoot() == null || matcher.test(visitorInfo)) {
                    v.visit(f, relativePath);
