        "core/src/main/java/hudson/util/ProcessData.java": "package hudson.util;

import java.util.ArrayList;
import java.util.Collections;
import java.util.List;

public class ProcessData {
    private int ppid = -1;
    private EnvVars envVars;
    private List<String> arguments;

    public int getPpid() {
        return ppid;
    }

    public void setPpid(int ppid) {
        this.ppid = ppid;
    }

    public EnvVars getEnvVars() {
        return envVars;
    }

    public void setEnvVars(EnvVars envVars) {
        this.envVars = envVars;
    }

    public List<String> getArguments() {
        return arguments;
    }

    public void setArguments(List<String> arguments) {
        this.arguments = arguments;
    }

    public void initializeArguments() {
        if (this.arguments == null) {
            this.arguments = new ArrayList<>();
        }
    }

    public void initializeArguments(int size) {
        if (this.arguments == null) {
            this.arguments = new ArrayList<>(size);
        }
    }

    public void addArgument(String arg) {
        if (this.arguments == null) {
            this.arguments = new ArrayList<>();
        }
        this.arguments.add(arg);
    }

    public void finalizeArguments() {
        if (this.arguments != null) {
            this.arguments = Collections.unmodifiableList(this.arguments);
        }
    }

    public void initializeEnvVars() {
        if (this.envVars == null) {
            this.envVars = new EnvVars();
        }
    }

    public void addEnvVarLine(String line) {
        if (this.envVars == null) {
            this.envVars = new EnvVars();
        }
        this.envVars.addLine(line);
    }

    // Additional methods to manipulate ppid, envVars, and arguments can be added here
}
