import java.util.*;
import org.spockframework.mock.*;
import org.spockframework.util.*;

public class MockConfigurator {
  public <T> T createMock(@Nullable String name, Class<T> type, MockNature nature, Map<String, Object> options) {
    return MockUtil.createDetachedMock(name, type, nature, MockImplementation.JAVA, options, getClassLoader(type));
  }

  public <T> T createMock(@Nullable String name, T obj, MockNature nature, Map<String, Object> options) {
    return MockUtil.createDetachedMock(name, obj, nature, MockImplementation.JAVA, options, getClassLoader(obj));
  }

  private ClassLoader getClassLoader(Class<?> type) {
    ClassLoader classLoader = type.getClassLoader();
    return classLoader != null ? classLoader : ClassLoader.getSystemClassLoader();
  }

  private ClassLoader getClassLoader(Object obj) {
    return getClassLoader(obj.getClass());
  }
}
