  private Specification sharedInstance;

  private Specification currentInstance;

  private UniqueId parentId;

  private ErrorInfoCollector errorInfoCollector;

  private StoreProvider storeProvider;

  private SpecificationContext specificationContext;

  public SpockExecutionContext(EngineExecutionListener engineExecutionListener) {
    this.engineExecutionListener = engineExecutionListener;
    this.storeProvider = StoreProvider.createRootStoreProvider();
  }

  public SpockExecutionContext setRunContext(RunContext runContext) {
    this.runContext = runContext;
    return this;
  }

  public SpockExecutionContext setRunner(PlatformParameterizedSpecRunner runner) {
    this.runner = runner;
    return this;
  }

  public SpecificationContext getSpecificationContext() {
    if (specificationContext == null) {
      specificationContext = new SpecificationContext(currentFeature != null && currentFeature.isParameterized(),
        errorInfoCollector.hasErrors() ? errorInfoCollector.getLastError().getException() : null);
      ((MockObject)specificationContext.getCurrentMock()).setContext(specificationContext);
      specificationContext.setCurrentDataVariables(currentFeature == null ? Map.of() : currentFeature.getCurrentDataVariables());
    }
    return specificationContext;
  }

  public SpockExecutionContext setSpecificationContext(SpecificationContext specificationContext) {
    this.specificationContext = specificationContext;
    return this;
