              "newContent": "public class MockConfiguration implements IMockConfiguration {
private final MockConfigurationData configurationData;

public MockConfiguration(@Nullable String name, Type type, MockNature nature,
    MockImplementation implementation, Map<String, Object> options) {
    this(new MockConfigurationData(name, type, null, nature, implementation, options));
}

public MockConfiguration(MockConfigurationData configurationData) {
  this.configurationData = configurationData;
  resolveOptions(configurationData.getOptions());
}

private void resolveOptions(Map<String, Object> options) {
  configurationData.setName(getOption(options, "name", String.class, configurationData.getName()));
  configurationData.setType(getOption(options, "type", Type.class, configurationData.getType()));
  configurationData.setInstance(getOption(options, "instance", Object.class, configurationData.getInstance()));
  configurationData.setNature(getOption(options, "nature", MockNature.class, configurationData.getNature()));
  configurationData.setImplementation(getOption(options, "implementation", MockImplementation.class, configurationData.getImplementation()));
  configurationData.setConstructorArgs(getOptionAsList(options, "constructorArgs"));
  configurationData.setAdditionalInterfaces(getOption(options, "additionalInterfaces", List.class, emptyList()));
  configurationData.setDefaultResponse(getOption(options, "defaultResponse", IDefaultResponse.class, configurationData.getNature().getDefaultResponse()));
  configurationData.setGlobal(getOption(options, "global", Boolean.class, false));
  configurationData.setVerified(getOption(options, "verified", Boolean.class, configurationData.getNature().isVerified()));
  configurationData.setUseObjenesis(getOption(options, "useObjenesis", Boolean.class, configurationData.getNature().isUseObjenesis()));
  configurationData.setMockMakerSettings(getOption(options, "mockMaker", IMockMakerSettings.class, null));
