        int x1=4;
        int y1=3;
        int z1=-80;
        if(params.getSign()){
            z1=z1+params.getExponent();
        }
        else{
            x1=(int)(x1*params.getMantissa());
        }
        stuff.printMax(new MathStuffParams(x1,y1,z1));
    }

